CREATE LOCAL TEMPORARY TABLE VOL_C_A ON COMMIT PRESERVE ROWS
AS(
  SELECT P1.BIL_ASSET_ROW_ID ASSET_ROW_ID
		,SUM(COALESCE(P1.UP_VOL,0)+COALESCE(P1.DOWN_VOL,0))/1024/1024 VOL   --M
     FROM ZJBIC.NET_CDR_DAY_C_DS_A        P1 
