CREATE LOCAL TEMPORARY TABLE CALL_C_A ON COMMIT PRESERVE ROWS
AS(
   SELECT  P1.BIL_ASSET_ROW_ID ASSET_ROW_ID
          ,SUM(COALESCE(P1.DUR     ,0))/60 CALL_DUR          --单位：MIN   
